mapper.select:
SELECT password
FROM user
WHERE username = #{username}

SELECT role
FROM user
WHERE username = #{username}

mapper.update:
UPDATE user
SET password = #{password}
WHERE username = #{username}

UPDATE user
SET ban = 1
WHERE username = #{username}

mapper.select:
SELECT ban
FROM user
WHERE username = #{username}

SELECT permission
FROM user
WHERE username = #{username}
